CREATE TABLE IF NOT EXISTS `wpbk_woocommerce_tax_rate_locations` (  `location_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `location_code` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,  `tax_rate_id` bigint(20) unsigned NOT NULL,  `location_type` varchar(40) COLLATE utf8mb4_unicode_ci NOT NULL,  PRIMARY KEY (`location_id`),  KEY `tax_rate_id` (`tax_rate_id`),  KEY `location_type_code` (`location_type`(10),`location_code`(20))) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
DROP TABLE IF EXISTS `wpbk_woocommerce_tax_rates`;
CREATE TABLE IF NOT EXISTS `wpbk_woocommerce_tax_rates` (  `tax_rate_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `tax_rate_country` varchar(2) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',  `tax_rate_state` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',  `tax_rate` varchar(8) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',  `tax_rate_name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',  `tax_rate_priority` bigint(20) unsigned NOT NULL,  `tax_rate_compound` int(1) NOT NULL DEFAULT 0,  `tax_rate_shipping` int(1) NOT NULL DEFAULT 1,  `tax_rate_order` bigint(20) unsigned NOT NULL,  `tax_rate_class` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',  PRIMARY KEY (`tax_rate_id`),  KEY `tax_rate_country` (`tax_rate_country`),  KEY `tax_rate_state` (`tax_rate_state`(2)),  KEY `tax_rate_class` (`tax_rate_class`(10)),  KEY `tax_rate_priority` (`tax_rate_priority`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
DROP TABLE IF EXISTS `wpbk_yith_wcwl`;
CREATE TABLE IF NOT EXISTS `wpbk_yith_wcwl` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `prod_id` int(11) NOT NULL,  `quantity` int(11) NOT NULL,  `user_id` int(11) NOT NULL,  `wishlist_id` int(11) DEFAULT NULL,  `dateadded` timestamp NOT NULL DEFAULT current_timestamp(),  PRIMARY KEY (`ID`),  KEY `prod_id` (`prod_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `wpbk_yith_wcwl_lists`;
CREATE TABLE IF NOT EXISTS `wpbk_yith_wcwl_lists` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `user_id` int(11) NOT NULL,  `wishlist_slug` varchar(200) NOT NULL,  `wishlist_name` text DEFAULT NULL,  `wishlist_token` varchar(64) NOT NULL,  `wishlist_privacy` tinyint(1) NOT NULL DEFAULT 0,  `is_default` tinyint(1) NOT NULL DEFAULT 0,  `dateadded` timestamp NOT NULL DEFAULT current_timestamp(),  PRIMARY KEY (`ID`),  UNIQUE KEY `wishlist_token` (`wishlist_token`),  KEY `wishlist_slug` (`wishlist_slug`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
